    margin-top: 2rem;
    padding: 1rem;
    text-align: center;
`;

const TextoCotizacion = styled.p `
    color: #00838F;
    padding: 1rem;
    text-transform: uppercase;
    font-weight: bold;
    margin: 0;
`;

const Resultado = ({cotizacion}) => {

    return (
        (cotizacion === 0) 
        ? 
        <Mensaje>Elige marca, a√±o y tipo de seguro</Mensaje> 
        : 
        <p>El total es: $ {cotizacion}</p>
    
    )
}
 
export default Resultado;
